import React, { PureComponent, Fragment } from 'react';
import HeroHeader from '../HeroHeader/HeroHeader';
import HeroBody from './../HeroBody/HeroBody'
import '../HeroHeader/HeroTest.scss';
import {Route} from 'react-router-dom'

class HeroTest extends PureComponent {
    state = {
        headerAppearance: "App-header",
        clearDisplay: false,
        route:"/",
        currentComponent: null,
    }

    currentComponent = (<HeroHeader headerCssPassed={this.state.headerAppearance} takeTestButtonClicked={this.takeTestButtonClickedHandler} clearDisplay={this.state.clearDisplay}/>);

    takeTestButtonClickedHandler = () => {
        this.currentComponent = <HeroBody/>
        let answer = this.state.takeTheTest;
        this.setState(
            {headerAppearance: "altApp-Header"}
        );
        setTimeout(()=>{ 
            this.setState({
            takeTheTest: !answer,
            route: "intro"}); 
            alert("Intro")}, 2000);
    }

    clearDisplayHandler = () => {
        this.setState({
            clearDisplay:{
                display: true
            }
        });
    }

    render(){

        return(
            <Fragment>
                <br/>
                <Route exact path={this.state.route} render={ () => this.currentComponent} />
            </Fragment>
        );
    }
}

export default HeroTest;
