import React, { PureComponent } from 'react';
import Wizard from './../../../assets/burgerIcon2.png';
import './HeroBody.scss';
import Questions from './../../../questions.json'

class HeroBody extends PureComponent{
    state= {
        showContent: "questionText",
        showButton: "myButton",
        wizardStyle: "wizard",
        intro: Questions.intro,
        introCount: 0,
    }

    onClickHandler = () => {
        let count = this.state.introCount + 1;
        if(count < this.state.intro.length)
            this.setState({
                introCount: count
            });
    }


    render() {
        return(
            <div className="questionContentBox">
                <img src={Wizard} alt="wizard" className={this.state.wizardStyle}></img>
                <p className = {this.state.showContent}>
                    {this.state.intro[this.state.introCount]}
                </p>
                <button onClick={this.onClickHandler} className="myButton">Continue</button>
            </div>
        );
    }
}

export default HeroBody;
