import React, { PureComponent, Fragment } from 'react';
import HeroHeader from '../HeroHeader/HeroHeader';
import '../HeroHeader/HeroTest.scss';
import {Route} from 'react-router-dom'

class HeroTest extends PureComponent {
    state = {
        headerAppearance: "App-header",
        headerTitle: true,
        clearDisplay: false,
        route:"/"
    }

    takeTestButtonClickedHandler = () => {
        let answer = this.state.takeTheTest;
        let showTitle = this.state.headerTitle;
        this.setState(
            {headerAppearance: "altApp-Header"}
        );
        setTimeout(()=>{alert("Did this really work?")}, 4000);

        this.setState({
            takeTheTest: !answer,

            route: "intro",
            headerTitle: !showTitle,
        })
    }

    clearDisplayHandler = () => {
        this.setState({
            clearDisplay:{
                display: true
            }
        });
    }

    render(){
        return(
            <Fragment>
                <br/>
                <Route exact path={this.state.route} render={ () =>
                <HeroHeader
                    showTitle={this.state.headerTitle} 
                    headerCssPassed={this.state.headerAppearance} 
                    takeTestButtonClicked={this.takeTestButtonClickedHandler}
                    clearDisplay={this.state.clearDisplay}/>} />
            </Fragment>
        );
    }
}

export default HeroTest;
