import React, { PureComponent } from 'react';
import './HeroBody.scss';
import Questions from './../../../questions.json'

class HeroBody extends PureComponent{
    state= {
        showContent: "questionText",
        showButton: "myButton",
        wizardStyle: "wizard",
        questions: Questions.questions,
        introCount: 0,
        questionCount: 0,
        buttonText: "Continue",
        endStyle: {},
        agreement: ""
    }

    onClickHandler = () => {
        let count = this.state.introCount + 1;
        if(count < this.state.questions.length){
            this.setState({
                showContent: "altQuestionText"
            });
            setTimeout(() => {
                this.setState({
                    introCount: count,
                    showContent: "questionText",
                    agreement: ""
                })
            },500);
            console.log("count: ", count);
            console.log(this.state.questions.length)
        }
        if(count+6 >= this.state.questions.length)
            this.setState({
                endStyle: {transform: "scale(1.5)", backgroundColor: "gold"},
                buttonText: "Results",
            });
    }

    onRadioOptionChange = (event) => {
        console.log("Got into handler");
        if(event.target.checked)
            this.setState({
                agreement: event.target.value
            });
    }


    render() {
        console.log("This is a render");

        console.log(this.state.agreement);

        return(
            <div className="questionContentBox">
                    <div className="row">
                        <div className="col-1-of-1">
                            <p className = {this.state.showContent}>
                                {this.state.questions[this.state.introCount].question}
                            </p>
                        </div>
                    </div>
                    <div className="row answer">
                        <div className="col-1-of-1">
                            <form>
                                <span style={{textShadow: "2px 2px 4px black", float: "left"}}>Strongly Agree</span>

                                <label>
                                    <input
                                    style={{height:"4rem", width: "4rem"}}
                                    type="radio" 
                                    value="StronglyAgree" 
                                    onChange={this.onRadioOptionChange.bind(this)}
                                    checked={this.state.agreement === "StronglyAgree"}/> </label>
                                <label>
                                    <input 
                                    style={{height:"3rem", width: "3rem"}}
                                    type="radio" 
                                    value="Agree" 
                                    onChange={this.onRadioOptionChange.bind(this)}
                                    checked={this.state.agreement === "Agree"}/> </label>
                                <label>
                                    <input
                                    style={{height:"2rem", width: "2rem"}}
                                    type="radio" 
                                    value="Undecided" 
                                    onChange={this.onRadioOptionChange.bind(this)}
                                    checked={this.state.agreement === "Undecided"}/> </label>
                                <label>
                                    <input 
                                    style={{height:"3rem", width: "3rem"}}
                                    type="radio" 
                                    value="Disagree" 
                                    onChange={this.onRadioOptionChange.bind(this)}
                                    checked={this.state.agreement === "Disagree"}/> </label>
                                <label>
                                    <input 
                                    style={{height:"4rem", width: "4rem"}}
                                    type="radio" 
                                    value="StronglyDisagree" 
                                    onChange={this.onRadioOptionChange.bind(this)}
                                    checked={this.state.agreement === "StronglyDisagree"}/> </label>
                                <span style={{textShadow: "2px 2px 4px black", float: "right"}}>Strongly Disagree</span>
                            </form>
                        </div>
                    </div>
                    <div className="row" style={{boxShadow:"none", backgroundImage: "none"}}>
                        <div className="col-1-of-1">
                            <button className="questionButton" onClick={this.onClickHandler}>Continue</button>
                        </div>
                    </div>
            </div>

        );
    }
}



export default HeroBody;
