import React, { PureComponent } from 'react';
import './HeroBody.scss';
import Questions from './../../../questions.json'

class HeroBody extends PureComponent{
    state= {
        showContent: "questionText",
        showButton: "myButton",
        wizardStyle: "wizard",
        intro: Questions.intro,
        introCount: 0,
        questionCount: 0,
        buttonText: "Continue",
        endStyle: {}
    }

    onClickHandler = () => {
        let count = this.state.introCount + 6;
        if(count < this.state.intro.length){
            this.setState({
                showContent: "altQuestionText"
            });
            setTimeout(() => {
                this.setState({
                    introCount: count,
                    showContent: "questionText"
                })
            },500);
            console.log("count: ", count);
            console.log(this.state.intro.length)
        }
        if(count+6 >= this.state.intro.length)
            this.setState({
                endStyle: {transform: "scale(1.5)", backgroundColor: "gold"},
                buttonText: "Results",
            });
    }


    render() {
        return(
            <div className="questionContentBox">
                <p className = {this.state.showContent}>
                    {this.state.intro[this.state.introCount]}
                </p>
                <p className = {this.state.showContent}>
                    {this.state.intro[this.state.introCount+1]}
                </p>
                <p className = {this.state.showContent}>
                    {this.state.intro[this.state.introCount+2]}
                </p>
                <p className = {this.state.showContent}>
                    {this.state.intro[this.state.introCount+3]}
                </p>
                <p className = {this.state.showContent}>
                    {this.state.intro[this.state.introCount+4]}
                </p>
                <p className = {this.state.showContent}>
                    {this.state.intro[this.state.introCount+5]}
                </p>
        <button style={this.state.endStyle}onClick={this.onClickHandler} className="questionButton">{this.state.buttonText}</button>
            </div>
        );
    }
}

export default HeroBody;
