import React, { PureComponent } from 'react';
import './HeroBody.scss';
import Questions from './../../../questions.json'

class HeroBody extends PureComponent{
    state= {
        showContent: "questionText",
        showButton: "myButton",
        wizardStyle: "wizard",
        questions: Questions.questions,
        introCount: 0,
        questionCount: 0,
        buttonText: "Continue",
        strongStyle: {border: "5px solid red",marginRight: "1rem" ,height: "4rem", width: "4rem"},
        midStyle: {marginRight: "1rem" ,height:"3rem", width: "3rem"},
        neutralStyle: {marginRight: "1rem" ,height:"2rem", width: "2rem"},
        endStyle: {height: "4rem", width: "4rem"},
        agreement: "",
        backgroundGradient: "radial-gradient(rgba(255, 0, 247, 0.63), rgba(39, 233, 255, 0.637)), url('./../../../assets/fantasyScene.jpeg');"
    }

    onClickHandler = () => {
        let count = this.state.introCount + 1;
        if(count < this.state.questions.length){
            this.setState({
                showContent: "altQuestionText"
            });
            setTimeout(() => {
                this.setState({
                    introCount: count,
                    showContent: "questionText",
                    agreement: ""
                })
            },500);
            console.log("count: ", count);
            console.log(this.state.questions.length)
        }
        if(count+6 >= this.state.questions.length)
            this.setState({
                endStyle: {transform: "scale(1.5)", backgroundColor: "gold"},
                buttonText: "Results",
            });
    }

    onRadioOptionChange = (event) => {
        console.log("Got into handler");
        if(event.target.checked)
            this.setState({
                agreement: event.target.value,
            });
    }


    render() {
        console.log("This is a render");

        console.log(this.state.agreement);

        return(
            <div className="questionContentBox">
                    <div className="row">
                        <div className="col-1-of-1">
                            <p className = {this.state.showContent}>
                                {this.state.questions[this.state.introCount].question}
                            </p>
                        </div>
                    </div>

                        <div className="row answer">
                            <div className="col-1-of-3">
                                <span style={{float:"right", textShadow: ".1rem .1rem .5rem rgb(0, 0, 0)"}}>Strongly Agree</span> 
                            </div>
                            <div className="col-1-of-3">
                                <form>
                                    <label className="radio">
                                        <input
                                            type="radio" 
                                            value="StronglyAgree"
                                            onClick={() => console.log("SA Clicked")}
                                            onChange={this.onRadioOptionChange.bind(this)}
                                            checked={this.state.agreement === "StronglyAgree"}/>
                                        <span className="circle"></span>
                                    </label>
                                    <label className="radio">
                                        <input 
                                            style={this.state.midStyle}
                                            type="radio" 
                                            value="Agree" 
                                            onChange={this.onRadioOptionChange.bind(this)}
                                            checked={this.state.agreement === "Agree"}/>
                                            <span className="circle"></span>
                                    </label>
                                    <label className="radio">
                                        <input
                                            style={this.state.neutralStyle}
                                            type="radio" 
                                            value="Undecided" 
                                            onChange={this.onRadioOptionChange.bind(this)}
                                            checked={this.state.agreement === "Undecided"}/>
                                            <span className="circle"></span>
                                        </label>
                                    <label className="radio">
                                        <input 
                                            style={this.state.midStyle}
                                            type="radio" 
                                            value="Disagree" 
                                            onChange={this.onRadioOptionChange.bind(this)}
                                            checked={this.state.agreement === "Disagree"}/>
                                            <span className="circle"></span>
                                    </label>
                                    <label className="radio">
                                        <input 
                                            style={this.state.endStyle}
                                            type="radio" 
                                            value="StronglyDisagree" 
                                            onChange={this.onRadioOptionChange.bind(this)}
                                            checked={this.state.agreement === "StronglyDisagree"}/>
                                            <span className="circle"></span>
                                    </label>
                                </form>
                            </div>
                            <div className="col-1-of-3">
                                <span style={{float: "left", textShadow: "0rem 0rem 2rem rgb(0, 0, 0)"}}>Strongly Disagree</span>
                            </div>


                    </div>
                    <div className="row" style={{boxShadow:"none", backgroundImage: "none"}}>
                        <div className="col-1-of-1">
                            <button className="questionButton" onClick={this.onClickHandler}>Continue</button>
                        </div>
                    </div>
            </div>

        );
    }
}



export default HeroBody;
