import React, { PureComponent } from 'react';
import Wizard from './../../../assets/burgerIcon2.png';
class HeroBody extends PureComponent{
    state= {
        showContent: "questionText",
        showButton: "myButton",
        wizardStyle: "wizard",
        showFirst:'',
        showSecond:'',
        showThird:'',
        showQuestions:''
    }

    onClickHandler = () => {
        this.setState({
            showContent: "altQuestionText",
            showButton: "altQuestionText",
            wizardStyle: "wizardAfterClick"
        });
        this.props.clearDisplay();
        console.log("Got here");
    }


    render() {
        return(
            <div className="questionContentBox">
                <img src={Wizard} alt="wizard" className={this.state.wizardStyle}></img>
                <p className = {this.state.showContent}>
                    <br></br>
                    Welcome, visitor! This is the test of heroes. I realize that in <strong>your world</strong> magic 
                    does not exist. In our world, the <strong>real world,</strong> magic
                    is very much a part of our everyday lives.
                </p>
                <div className="row">
                    <div className="2-2-col">
                        2 even columns
                    </div>
                    <div className="3-3-col">
                        3 even columns
                    </div>
                    <div className="2-3-col">
                    2 columns, 1 1/3rd and 1 2/3rds
                    </div>
                    <div className="4-4-col">
                        4 even columns
                    </div>
                    <div className="3-4-col">
                        3 colums, 2 1/4th and 1 2/4th
                    </div>
                </div>
                <button onClick={this.onClickHandler} className="myButton">Continue</button>     
            </div>
        );
    }
}

export default HeroBody;
