import React, { PureComponent } from 'react';
import './HeroBody.scss';
import Questions from './../../../questions.json'

class HeroBody extends PureComponent{
    state= {
        showContent: "questionText",
        showButton: "myButton",
        wizardStyle: "wizard",
        questions: Questions.questions,
        introCount: 0,
        questionCount: 0,
        buttonText: "Continue",
        endStyle: {},
        agreement: "StronglyAgree"
    }

    onClickHandler = () => {
        let count = this.state.introCount + 6;
        if(count < this.state.questions.length){
            this.setState({
                showContent: "altQuestionText"
            });
            setTimeout(() => {
                this.setState({
                    introCount: count,
                    showContent: "questionText"
                })
            },500);
            console.log("count: ", count);
            console.log(this.state.questions.length)
        }
        if(count+6 >= this.state.questions.length)
            this.setState({
                endStyle: {transform: "scale(1.5)", backgroundColor: "gold"},
                buttonText: "Results",
            });
    }

    onRadioOptionChange = (event) => {
        console.log("Got into handler");
        if(event.target.checked)
            this.setState({
                agreement: event.target.value
            });
    }


    render() {
        console.log("This is a render");

        console.log(this.state.agreement);

        return(
            <div className="questionContentBox">
                <div className="row">
                    <div className="col-1-of-2">
                        <p className = {this.state.showContent}>
                            {this.state.questions[this.state.introCount].question}
                            <br></br><br></br>
                        </p>
                    </div>
                    <div className="col-1-of-2">
                        <form>
                            <p>Strongly Agree</p>
                            <label>
                                <input
                                style={{height:"4rem", width: "4rem"}}
                                type="radio" 
                                value="StronglyAgree" 
                                onChange={this.onRadioOptionChange.bind(this)}
                                checked={this.state.agreement === "StronglyAgree"}/> </label><br></br>
                            <label>
                                <input 
                                style={{height:"3rem", width: "3rem"}}
                                type="radio" 
                                value="Agree" 
                                onChange={this.onRadioOptionChange.bind(this)}
                                checked={this.state.agreement === "Agree"}/> </label><br></br>
                            <label>
                                <input
                                style={{height:"2rem", width: "2rem"}}
                                type="radio" 
                                value="Undecided" 
                                onChange={this.onRadioOptionChange.bind(this)}
                                checked={this.state.agreement === "Undecided"}/> </label><br></br>
                            <label>
                                <input 
                                style={{height:"3rem", width: "3rem"}}
                                type="radio" 
                                value="Disagree" 
                                onChange={this.onRadioOptionChange.bind(this)}
                                checked={this.state.agreement === "Disagree"}/> </label><br></br>
                            <label>
                                <input 
                                style={{height:"4rem", width: "4rem"}}
                                type="radio" 
                                value="StronglyDisagree" 
                                onChange={this.onRadioOptionChange.bind(this)}
                                checked={this.state.agreement === "StronglyDisagree"}/> </label><br></br>
                            <p>Strongly Disagree</p>
                        </form>
                    </div>
                </div>
                <div className="row">
                    <div className="col-1-of-1">
                        <button className="questionButton">Continue</button>
                    </div>
                </div>
            </div>
        );
    }
}

export default HeroBody;
